import axios from 'axios';
import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      rooms: [],
      devices: []
    }
  }

  componentDidMount() {
    this.getRooms();
    this.getDevices();
  }

  getRooms() {
    axios.get('http://localhost:4000/rooms')
      .then(r => this.setState({rooms: r.data}))
      .catch(err => console.log(err));
  }

  getDevices() {
    axios.get('http://localhost:4000/devices')
      .then(d => this.setState({devices: d.data.filter(d => d.isLight)}))
      .catch(err => console.log(err));
  }

  render() {
    const rooms = this.state.rooms;
    const devices = this.state.devices;
    return (
      <div className="App">
        <p>Found {rooms.length} rooms</p>
        <p>Found {devices.length} devices</p>
      </div>
    );
  }
}

export default App;
