import axios from 'axios';
import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      rooms: [],
      devices: []
    }
  }

  componentDidMount() {
    this.getRooms();
    this.getDevices();
  }

  getRooms() {
    axios.get('http://localhost:4000/rooms')
      .then(r => this.setState({rooms: r.data}))
      .catch(err => console.log(err));
  }

  getDevices() {
    axios.get('http://localhost:4000/devices')
      .then(d => this.setState({devices: d.data}))
      .catch(err => console.log(err));
  }

  render() {
    return (
      <div className="App">
      </div>
    );
  }
}

export default App;
